mapper.update:
UPDATE mandalart
SET title = #{title},
contents = #{contents},
depth = #{depth},
order_id = #{orderId},
completed_fg = #{completedFg},
start_date = #{startDate},
finish_date = #{finishDate},
parent_id = #{parentId}
WHERE project_id = #{projectId}
AND mandalart_id = #{mandalartId}
AND (#{startDate} IS NULL OR #{finishDate} IS NULL OR EXISTS (
SELECT 1
FROM mandalart parent
WHERE parent.mandalart_id = #{parentId}
AND parent.start_date  <  #{startDate}
AND parent.finish_date  >  #{finishDate}
));
